def decrypt(text, alp):
    temp_alp = dict.fromkeys(alp, 0)
    for c in text:
        if (c in alp):
            temp_alp[c]+= 1
    temp_alp = list(dict(sorted(temp_alp.items(), key=lambda item: item[1], reverse=True)).keys())

    rez=""

    for c in text:
        if (c in alp):
            c = alp[temp_alp.index(c)]
        rez = rez + c
    return rez

